property.code:
require.config({
    paths: {
        jstree: "$doc.getAttachmentURL('jstree.min')",
    },
    shim: {
        jstree: {
            export: "jstree"
        }
    }
});
require(['jQueryNoConflict', 'jstree'], function($, jstree) {
    var dynamicXWikiHierarchy = dynamicXWikiHierarchy || {};
    dynamicXWikiHierarchy = function() {
        var that = this;
        this.childrenIcon = "$xwiki.getSkinFile('icons/silk/page_white.png')";
        this.init = function(element) {
            console.log("Initializing tree...");
            console.log(resultPage);
            var roots_node_url = "$xwiki.getURL('Macros.DynamicHierarchyMacroJson', 'view', 'xpage=plain')&" + stringParams
            if(resultPage!=""){
                roots_node_url = resultPage + "&" + stringParams
            }
            var pluginsList = ["types"];
            if (showcheckboxes) {
                pluginsList.push("checkbox");
                $.jstree.defaults.checkbox.three_state = false;
            }
            $(element).on('changed.jstree', function(e, data) {
                if (data.hasOwnProperty('node') && data.node.hasOwnProperty('id')) {
                    if (!showcheckboxes) {
                        document.location = data.node.data;
                    }
                }
            }).on('select_node.jstree deselect_node.jstree', function(e, data) {
                if (showcheckboxes) {
                    $(element).find('.checkboxs').html(" ");
                    var i, j, r = [];
                    for (i = 0, j = data.selected.length; i < j; i++) {
                        $(element).find('.checkboxs').append('<input type="hidden" name="' + checkboxname + '" value="' + data.instance.get_node(data.selected[i]).id + '" />');
                    }
                }
            }).on('ready.jstree', function(e, data) {
              if (showcheckboxes) {
                $('<div class="checkboxs"></div>').appendTo(element);
              }
            }).jstree({
                "checkbox": {
                    "keep_selected_style": false
                },
                "plugins": pluginsList,
                'core': {
                    'strings': {
                        'Loading ...': "$msg.get('hierarchy.core.loading')"
                    },
                    "multiple": true,
                    "animation": 1,
                    'data': {
                        url: roots_node_url,
                        'data': function(node) {
                            return {
                                'id': node.id
                            };
                        }
                    }
                }
            });
        }
    };
    var dynamicXWikiHierarchy = new dynamicXWikiHierarchy();
    dynamicXWikiHierarchy.init("#tree");
});

